import { Suspense, useEffect, useRef } from "react";
import * as THREE from "three/src/Three";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";

import { Canvas, useFrame, useLoader } from "@react-three/fiber";
import {
  useGLTF,
  PresentationControls,
  Environment,
  ContactShadows,
  Html,
  useFBX,
  useHelper,
} from "@react-three/drei";
import MyApp from "../../App";
import Model from "./Lowpolyroom1";
import StarterScreen from "../StarterScreen";
import useKeyPress from "../useKeyPress";
import { useNavigate } from "react-router-dom";
import RoomWithComputer from "./Roomwithcomputer";
import LowPolyRoom2 from "./Lowpolyroom2";
import PointLightRoom from "./Pointlightroom";
import LowPolyRoom3 from "./Lowpolyroom3";

function SceneSpotlight() {
  const light = useRef();
  useHelper(light, THREE.SpotLightHelper, "cyan");
  return (
    <group>
      <spotLight
        ref={light}
        color={"#8800ff"}
        position={[0.1, 0.3, 0.3]}
        angle={30}
        penumbra={1}
        castShadow
        shadow-mapSize={[512, 512]}
        shadowBias={-0.002}
        shadowCameraNear={1}
        shadowCameraFar={4}
        intensity={2}
      />
    </group>
  );
}

function RectLight({ position }) {
  const light = useRef();
  // useHelper(light, THREE.RectAreaLight, "red");
  return (
    <group>
      <rectAreaLight
        color={"#0000ff"}
        width={1}
        height={0.11}
        ref={light}
        position={[0.2, 0.7, 0.1]}
        intensity={0.5}
      />
    </group>
  );
}

export default function RoomScene() {
  const navigate = useNavigate();
  const l = useKeyPress("l");
  const f = useKeyPress("f");
  const g = useKeyPress("g");
  const light = useRef();

  useEffect(() => {
    if (l && f && g) {
      navigate("/whitepaper");
    }
  }, [l, f, g]);
  return (
    <Suspense fallback={<div>:)</div>}>
      <Canvas shadows dpr={[4, 3]} camera={{ position: [0, 0, 0], fov: 50 }}>
        {/* <ambientLight intensity={0.5} /> */}

        <PresentationControls
          global
          config={{ mass: 2, tension: 500 }}
          // snap={{ mass: 4, tension: 1500 }}
          rotation={[0, 0, 0]}
          // polar={[-Math.PI / 3, Math.PI / 3]}
          // azimuth={[-Math.PI / 1.4, Math.PI / 2]}
          polar={[-Math.PI, Math.PI]}
          azimuth={[-Math.PI, Math.PI]}
        >
          <RectLight />
          {/* <SceneSpotlight /> */}
          {/* <spotLight
            color={"#8800ff"}
            position={[-0.53, 0.5, -0.64]}
            angle={0.15}
            penumbra={1}
            shadow-mapSize={[512 * 32, 512 * 32]}
            castShadow
            ref={light}
          /> */}
          {/* <Html
            scale={0.0055}
            rotation={[0, 0, 0]}
            position={[-0.085, 0, -0.2]}
            transform
            occlude
          >
            <MyApp />
          </Html> */}
          {/* <Html
            scale={0.009}
            rotation={[0, 0, 0]}
            position={[-0.0695, 0.0375, -0.84]}
            transform
            occlude
          >
            <StarterScreen l={l} f={f} g={g} />
          </Html> */}
          {/* <Html
            scale={0.0036}
            rotation={[0, -Math.PI / 16, 0]}
            position={[0.114, 0.0115, -0.31]}
            transform
            occlude
          >
            <div style={{}}>
              <iframe
                src="https://opensea.io/explore-collections?tab=top"
                title={"display"}
                height={1000}
                width={1000}
              ></iframe>
            </div>
          </Html> */}
          {/* <Model position={[0.25, -0.6, 0]} castShadow={true} /> */}
          <Html
            scale={0.0075}
            rotation={[0, 0, 0]}
            position={[-0.053, 0.075, -0.64]}
            transform
          >
            <StarterScreen l={l} f={f} g={g} />
          </Html>
          {/* <RoomWithComputer position={[0.25, -0.6, 0]} castShadow={true} /> */}
          {/* <LowPolyRoom /> */}
          <LowPolyRoom3 position={[0.42, -0.62, 0]} castShadow={true} />
          {/* <Watch rotation={[0, 0, 0]} position={[0.1, -0.2, 0]} scale={0.003} /> */}
        </PresentationControls>
        <ContactShadows
          rotation-x={Math.PI / 2}
          position={[0, -1.4, 0]}
          opacity={0.75}
          width={10}
          height={10}
          blur={2.6}
          far={2}
        />
        <Environment preset="city" />
      </Canvas>
    </Suspense>
  );
}

function LowPolyRoom() {
  const fbx = useFBX("/lowpolyroom1.fbx");
  return (
    <mesh scale={1} position={[42, -62, 0]}>
      <primitive object={fbx} dispose={null} />
    </mesh>
  );
}

function Watch(props) {
  const ref = useRef();
  const { scene } = useLoader(
    GLTFLoader,
    "https://collectees.mypinata.cloud/ipfs/QmPj7tZRQrkbAish8c89hUYfXMzFWv33aguz3tuVWHhSLT/lowpolyroom1.glb"
  );
