    // ref.current.position.y = (1 + Math.sin(t / 1.5)) / 10;
  });
  // console.log("NODES: ", nodes);
  // console.log("Materials: ", materials);

  return (
    <group ref={ref} {...props} dispose={null}>
      {/* <mesh
